mapper.select:
select * from
	  ( 
		select S.*,ROWNUM RN from (
			select  ID id,SKU sku,CATAGORIES catagories ,PARENT_CATAGORIES parentCatagories from MAPPING_SKU_INFO where ENABLED_FLAG='Y'   order by CREATE_DATE desc 
		) S 

		where ROWNUM<=#{endRow}
	)
		WHERE RN>=#{startRow}

mapper.insert:
INSERT INTO MAPPING_SKU_INFO(ID,SKU,CATAGORIES,PARENT_CATAGORIES,CREATE_BY,CREATE_DATE) select mapping_sku_SEQ.nextval,c.*,sysdate from(
select #{item.sku,jdbcType=VARCHAR}, #{item.catagories,jdbcType=VARCHAR}, #{item.parentCatagories,jdbcType=VARCHAR}, #{item.createBy,jdbcType=BIGINT} from dual
)c
