mapper.update:
update ebay_message set
text = #{Text,jdbcType=VARCHAR},
content =  #{Content,jdbcType=VARCHAR}
where message_id = #{MessageID,jdbcType=VARCHAR}

mapper.select:
select * from
  ( 
	select S.*,ROWNUM RN from (


		order by em.receive_date desc
	) S 

	where ROWNUM<=#{endRow}
)
	WHERE RN>=#{startRow}

select * from
  ( 
	select S.*,ROWNUM RN from (


		order by em.creationdate desc
	) S 

	where ROWNUM<=#{endRow}
)
	WHERE RN>=#{startRow}

mapper.insert:
begin update ebay_message_member t set t.displaytopublic = #{isView},t.messagestatus ='Answered' where t.recipientid = #{recipientID} and t.senderid = #{senderID}
and t.itemid = #{itemID}
and t.itemid is null
; update ebay_message e set e.replied = 1 where e.external_message_id = #{messageId,jdbcType=VARCHAR} and e.recipient_user_id = #{recipientID,jdbcType=VARCHAR} and e.sender != 'eBay'; insert into ebay_message_answer (menber_id, message_id, answer, answer_date, id, ebay_account, item_id, sender_id, recipient_id, message_type, subject, MEDIA) values (#{id}, #{messageId,jdbcType=VARCHAR}, #{answer}, to_timestamp(#{answerDate,jdbcType=VARCHAR},'yyyy-MM-dd HH24:MI:ss.ff'), ebay_message_member_seq.nextval, #{recipientID,jdbcType=VARCHAR}, #{itemID,jdbcType=VARCHAR}, #{recipientID,jdbcType=VARCHAR}, #{senderID,jdbcType=VARCHAR}, 'answer', #{subject,jdbcType=VARCHAR}, #{media,jdbcType=VARCHAR} ); end;
